function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var bs = g_form.getReference('business_service', function(bs) { // I hope this is business service field (mention the field back end name and not the table name)
alert(bs.busines_criticality); // check what value it is returning 
        if (bs.busines_criticality == '1 - most critical') {
            g_form.setValue('impact', "1 - High");
            g_form.setValue('urgency', "1 - High");
            g_form.setValue('priority', "1 - Critical");
        }

    });

}
